import React, { useState } from "react";
import './ArtistDetailModal.css'

function ArtistDetailModal(props) {

    const [show, setShow] = useState(true)

    function hideModal() {
        setShow(false)
    }

    return (
        <div>
            {show &&     
                <div id="overlay" onClick={hideModal}>
                    <div id="modal-body">
                        <h1>{props.name}</h1>
                    </div>
                </div>
            }
        </div>
    )
}

export default ArtistDetailModal
