import React, { useState } from "react";
import './ArtistDetailModal.css'

function ArtistDetailModal(props) {



    const [show, setShow] = useState(props.showHide)

    setInterval(() => {
    function hideModal() {
        setShow(false)
    }

    return (
        <div>
            {show &&     
                <div id="overlay" onClick={hideModal}>
                    <div id="modal-body">
                        <h1>{props.name}</h1>
                    </div>
                </div>
            }
        </div>
    )
}, 1000);
}

export default ArtistDetailModal
