import React from "react";
import Tag from "../Atoms/Tag/Tag";
import './BandTile.css'
import artists from "../../data";

function BandTile(props) {

    function mapGenreTags(artist) {
        return (
            <Tag text={this.artist.genre} />
        )
    }

    return(
        <div id="tile-body">
            <div id="artist-img-container">
                <img id='artist-img' src={props.img} alt='artistPhoto'></img>
            </div>
            <div id="genre-list">
                {artists.map(mapGenreTags)}
            </div>
            <div id="artist-labels">
                <h4 id="artist-name">{props.name}</h4>
                <p id="artist-location" className="small-body">{props.location}</p>
            </div>
        </div>
    )
}

export default BandTile
