import React, { useState } from "react";
import './BandsSelection.css'

// components
import BandTile from "../Components/BandTile/BandTile";

// data
import artists from "../data";
import SearchField from "../Components/Atoms/Input/Search/SearchField";
import ArtistDetailModal from "../Components/Modals/ArtistDetailModal/ArtistDetailModal";

function BandsSelection() {

    const [artistDetailModal, setArtistDetailModal] = useState(false)

    function openArtistDetailModal() {
        setArtistDetailModal(true)
    }

    function getSelection(artist) {
        return ( 
            <div>
                {artistDetailModal && <ArtistDetailModal name={'test'}/>}
                <div onClick={openArtistDetailModal}>
                    <BandTile 
                        name={artist.name} 
                        location={artist.location} 
                        img={artist.img} 
                        genre={artist.genre}/>
                </div>
            </div>
        )

    }

    return (
        <div>
        <SearchField placeholder='test' />
        <div id="grid-container">

                {artists.map(getSelection)}
                </div>
        </div>
    )
}

export default BandsSelection
